00246     EB_FLAGS_T action() {
00247         switch (flags & 0b111111111) {
00248             case (B_PRS | B_PRS_R):
00249                 return EB_PRESS;
00250             case (B_PRS | B_HLD | B_HLD_R):
00251                 return EB_HOLD;
00252             case (B_PRS | B_HLD | B_STP | B_STP_R):
00253                 return EB_STEP;
00254             case (B_REL | B_REL_R):
00255             case (B_REL | B_REL_R | B_HLD):
00256             case (B_REL | B_REL_R | B_HLD | B_STP):
00257                 return EB_RELEASE;
00258             case (B_REL_R):
00259                 return EB_CLICK;
00260             case (B_CLKS_R):
00261                 return EB_CLICKS;
00262             case (B_REL_R | B_HLD):
00263                 return EB_REL_HOLD;
00264             case (B_CLKS_R | B_HLD):
00265                 return EB_REL_HOLD_C;
00266             case (B_REL_R | B_HLD | B_STP):
00267                 return EB_REL_STEP;
00268             case (B_CLKS_R | B_HLD | B_STP):
00269                 return EB_REL_STEP_C;
00270             default:
00271                 return EB_NONE;
00272         }
00273     }
