00353     // ====================== POLL ======================
00354     bool tick(VirtButton& b0, VirtButton& b1) {
00355         if (read_btn_flag(B_BOTH)) {
00356             if (!b0.pressing() && !b1.pressing()) clr_btn_flag(B_BOTH);
00357             if (!b0.pressing()) b0.reset();
00358             if (!b1.pressing()) b1.reset();
00359             b0.clear();
00360             b1.clear();
00361             return tick(1);
00362         } else {
00363             if (b0.pressing() && b1.pressing()) set_btn_flag(B_BOTH);
00364             return tick(0);
00365         }
00366     }
