00391     bool pollBtn(bool s) {
00392         if (read_btn_flag(B_BISR)) {
00393             clr_btn_flag(B_BISR);
00394             s = 1;
00395         } else s ^= read_btn_flag(B_INV);
00396 
00397         if (!read_btn_flag(B_BUSY)) {
00398             if (s) set_btn_flag(B_BUSY);
00399             else return 0;
00400         }
00401 
00402         uint16_t ms = EB_UPTIME();
00403         uint16_t deb = ms - timer;
00404 
00405         if (s) {                                      
00406             if (!read_btn_flag(B_PRS)) {                  
00407                 if (!read_btn_flag(B_DEB) && EB_DEB_T) {  
00408                     set_btn_flag(B_DEB);                   
00409                     timer = ms;                         
00410                 } else {                                
00411                     if (deb >= EB_DEB_T || !EB_DEB_T) { 
00412                         set_btn_flag(B_PRS | B_PRS_R);      
00413 #ifndef EB_NO_PEDOMETER␍
00414                         run_timer = ms;
00415 #endif␍
00416                         timer = ms;  
00417                     }
00418                 }
00419             } else {  
00420                 if (!read_btn_flag(B_EHLD)) {
00421                     if (!read_btn_flag(B_HLD)) {  
00422 #ifdef EB_HOLD_TIME␍
00423                         if (deb >= (uint16_t)EB_HOLD_T) {  
00424 #else␍
00425                         if (deb >= (uint16_t)(EB_HOLD_T << EB_SHIFT)) {  
00426 #endif␍
00427                             set_btn_flag(B_HLD_R | B_HLD); 
00428                             timer = ms;                 
00429                         }
00430                     } else {  
00431 #ifdef EB_STEP_TIME␍
00432                         if (deb >= (uint16_t)(read_btn_flag(B_STP) ? EB_STEP_T : EB_HOLD_T)) {
00433 #else␍
00434                         if (deb >= (uint16_t)(read_btn_flag(B_STP) ? (EB_STEP_T << EB_SHIFT) : (EB_HOLD_T << EB_SHIFT))) {
00435 #endif␍
00436                             set_btn_flag(B_STP | B_STP_R);  
00437                             timer = ms;                   
00438                         }
00439                     }
00440                 }
00441             }
00442         } else {                                     
00443             if (read_btn_flag(B_PRS)) {                 
00444                 if (deb >= EB_DEB_T) {               
00445                     if (!read_btn_flag(B_HLD)) clicks++;    
00446                     if (read_btn_flag(B_EHLD)) clicks = 0;  
00447                     set_btn_flag(B_REL | B_REL_R);        
00448                     clr_btn_flag(B_PRS);                   
00449                 }
00450             } else if (read_btn_flag(B_REL)) {
00451                 if (!read_btn_flag(B_EHLD)) {
00452                     set_btn_flag(B_REL_R);
00453                 }
00454                 clr_btn_flag(B_REL | B_EHLD);
00455                 timer = ms;       
00456             } else if (clicks) {  
00457 #ifdef EB_CLICK_TIME␍
00458                 if (read_btn_flag(B_HLD | B_STP) || deb >= (uint16_t)EB_CLICK_T) set_btn_flag(B_CLKS_R);  
00459 #else␍
00460                 if (read_btn_flag(B_HLD | B_STP) || deb >= (uint16_t)(EB_CLICK_T << EB_SHIFT)) set_btn_flag(B_CLKS_R);  
00461 #endif␍
00462 #ifndef EB_NO_PEDOMETER␍
00463                 else if (run_timer) run_timer = 0;
00464 #endif␍
00465             } else if (read_btn_flag(B_BUSY)) {
00466                 clr_btn_flag(B_HLD | B_STP | B_BUSY);
00467                 set_btn_flag(B_TOUT);
00468 #ifndef EB_NO_PEDOMETER␍
00469                 run_timer = 0;
00470 #endif␍
00471                 timer = ms;  // test!!
00472             }
00473             if (read_btn_flag(B_DEB)) clr_btn_flag(B_DEB);  
00474         }
00475         return read_btn_flag(B_CLKS_R | B_PRS_R | B_HLD_R | B_STP_R | B_REL_R);
00476     }
