00500     inline void set_btn_flag(const uint16_t x) __attribute__((always_inline)) {
00501         flags |= x;
00502     }
00503     inline void clr_btn_flag(const uint16_t x) __attribute__((always_inline)) {
00504         flags &= ~x;
00505     }
00506     inline bool read_btn_flag(const uint16_t x) __attribute__((always_inline)) {
00507         return flags & x;
00508     }
00509     inline void write_btn_flag(const uint16_t x, bool v) __attribute__((always_inline)) {
00510         if (v) set_btn_flag(x);
00511         else clr_btn_flag(x);
00512     }
00513     inline bool eq_btn_flag(const uint16_t x, const uint16_t y) __attribute__((always_inline)) {
00514         return (flags & x) == y;
00515     }
